app.get("/blogs", function (req, res, next) {
    res.send(BLOGS);
});
app.get("/blog/:id", function (req, res, next) {
    let blogID = Number(req.params.id);
    let index = BLOGS.findIndex(function findByIndex(blog) {
        return blog.id === blogID;
    });

    res.send(BLOGS[index]);
});

app.post("/addBlog", function (req, res, next) {
    let blog = req.body;
    BLOGS.push(blog);
    res.send(BLOGS);
});

app.put("/updateBlog/:id", function (req, res, next) {
    console.log(req.body);
    for (let i in BLOGS) {
        if (BLOGS[i].id == req.params.id) {
            BLOGS.splice(i, 1, req.body);
            break;
        }
    }
    res.send(BLOGS);
})

app.delete("/deleteBlog/:id", function(req, res, next) {
    for (let i in BLOGS) {
        if (BLOGS[i].id == req.params.id) {
            BLOGS.splice(i, 1);
            break;
        }
    }
    res.send(BLOGS);
})



app.listen(5000, function () {
    console.log("Server started on Port 5000...");
});
